```rust
// Write a programme for bubble sort

fn bubble_sort(mut arr: Vec<i32>) {
    let n = arr.len() - 1;
    let mut sorted: bool = false;

    while sorted == false {
        sorted = true;
        for i in 0..n {
            if arr[i + 1] < arr[i] {
                sorted = false;
                (arr[i], arr[i + 1]) = (arr[i + 1], arr[i]);
            }
        }
    }

    // this prints out the value of arr after bubble_sort ðŸ¤¯
    println!("After  Sort : {:?}", arr)
}

