```c
// Write a programme for linear search
#include <stdio.h>

int main()
{
    int n = 6;
    int input[] = {10, 20, 80, 30, 60, 50};
    int target = 30;

    for (int i = 0; i < n; i++)
    {
        if (input[i] == target)
        {
            printf("Found at index %d !!!\n", i);
            return 1;
        }
    }

    printf("Target Not Found !!!\n");

    return 0;
}

```

```cs
// Write a programme for linear search
using System;

public class Program
{
    public static void Main()
    {
        int[] input = { 10, 20, 80, 30, 60, 50 };
        int target = 30;
        int result = LinearSearch(input, target);
        if (result == -1)
            Console.WriteLine("Target Not Found");
        else
            Console.WriteLine("Target Found at Index : " + result);
    }

    public static int LinearSearch(int[] array, int target)
    {
        int n = array.Length - 1;
        for (int i = 0; i <= n; i++)
        {
            if (array[i] == target)
            {
                return i;
            }
        }

        return -1;
    }
}

```

```go
// Write a programme for Linear Search
package main

import "fmt"

func main() {
	var n, i, value, target int
	fmt.Printf("Enter number of elements in the array : ")
	fmt.Scan(&n)
	var array []int
	fmt.Printf("Enter %d elements : \n", n)
	for i = 0; i < n; i++ {
		fmt.Scan(&value)
		array = append(array, value)
	}

	fmt.Printf("\nEnter the value to search in the array : ")
	fmt.Scan(&target)

	value = -1
	for i = 0; i < n; i++ {
		if target == array[i] {
			value = i
			break
		}
	}

	if value == -1 {
		fmt.Printf("\n%d does not exists in the given array\n", target)
	} else {
		fmt.Printf("\n%d is located at index position %d\n", target, value)
	}
}

```

```java
