```python
# Write a programme to check a valid email address
import re

def solve(s):
    # pattern (starting with [a-zA-Z0-9-_] then @ then email domain)
    p = "^[a-zA-Z0-9]+@[a-zA-Z0-9]+\.[a-z]{1,3}$" 
    if re.match(p,s):
        return True
    else:
        for i in range(len(s)):
            if s[i] == '-':
                if s[i+1].isalpha() == False:
                    return False
            if s[i] == '.':
                if i != 0:
                    if s[i+1].isalpha() == True:
                        continue
                    return False
                return False
            if s[i] == '#':
                return False
        return True

s = input('Email : ')
print('Valid :', solve(s))

```

```js
// Write a programme to check a valid email address

function isValidEmail(email) {
  const re = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;
  if (email.match(re)) {
    return true;
  }
  return false;
}

const email = "test@gmail.com";

console.log("Input  :", email);
console.log("Output :", isValidEmail(email) ? "valid" : "invalid");

```

```cs
// Write a programme to check a valid email address
using System;
using System.Text.RegularExpressions;

public class EmailValidator
{
    public static bool IsValid(string email)
    {
        string regex =
            @"^([a-zA-Z0-9_\-\.]+)@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.)|(([a-zA-Z0-9\-]+\.)+))([azA-Z]{2,4}|[0-9]{1,3})(\]?)$";

        return Regex.IsMatch(email, regex, RegexOptions.IgnoreCase);
    }

    public static void Main()
    {
        string email = "abc@mail.com";
        Console.WriteLine("Input  : {0}", email);

        Console.WriteLine("Output : {0}", IsValid(email) ? "Valid" : "Invalid");
    }
}

```

