```cs
// Write a programme to convert degree to radian
using System;

public class DegreeToRadian
{
    static void Main(string[] args)
    {
        Console.Write("Input degrees : ");

        double degrees;
        while (!double.TryParse(Console.ReadLine(), out degrees))
        Console.WriteLine("Invalid Input!!");

        double radians = degrees * Math.PI / 180;
        Console
            .WriteLine($"\n{degrees} Degrees is {Math.Round(radians, 5)} Radians.");
    }
}

```

```js
// Write a programme to convert degree to radian

function degrees_to_radians(degrees) {
  var pi = Math.PI;
  return degrees * (pi / 180);
}

degree = 1;
console.log("Input  : " + degree);
console.log("Output : " + degrees_to_radians(degree).toFixed(5));

```

```java
// Write a program to convert Degrees into Radians
import java.util.Scanner;

public class DegreeToRadian {

  public static void main(String[] args) {
    Scanner sc = new Scanner(System.in);

    // taking degrees input
    System.out.print("Enter the amount of Degrees  : ");
    int degree = sc.nextInt();

    // calculating degree to radian
    double radian = degree * ((Math.PI) / 180);

    // printing the final answer
    System.out.printf("\nAmount in radians = %.5f\n", radian);

    sc.close();
  }
}

```

