```cs
// Write a programme to convert degree to radian
using System;

public class RadToDeg
{
    public static void Main(string[] args)
    {
        //Checking for valid input
        bool success = false;
        do
        {
            Console.Write("Input radians : ");
            try
            {
                RadianToDegree(Convert.ToDouble(Console.ReadLine()));
                success = true;
            }
            catch (System.Exception)
            {
                Console.WriteLine("Invalid Input!!");
            }
        }
        while (!success);
    }

    public static void RadianToDegree(double radians)
    {
        double degrees = (180 / Math.PI) * radians;
        Console
            .WriteLine($"\n{radians} Radians is {Math.Round(degrees, 5)} Degrees.");
    }
}

```

```java
// Write a program to convert Radian to Degree
import java.util.Scanner;

public class RadianToDegree {

  public static double radianToDegree(int radian) {
    return Math.round((radian * 180.0 / Math.PI) * 10000) / 10000.0;
  }

  public static void main(String[] args) {
    Scanner sc = new Scanner(System.in);

    // taking radians input
    System.out.print("Enter the amount of Radians  : ");
    int radian = sc.nextInt();

    // printing the answer
    System.out.printf("\nAmount in Degrees = %f\n", radianToDegree(radian));

    sc.close();
  }
}

```

