```java
// Write a programme to generate a random number in range n1-n2
import java.util.*;

class RandomNumber {

  public static void main(String[] args) {
    Scanner s_name = new Scanner(System.in);

    System.out.print("Enter start range : ");
    int startRange = s_name.nextInt();
    System.out.print("Enter end   range : ");
    int endRange = s_name.nextInt();

    System.out.print(
      "\nRandom value in int from " + startRange + " to " + endRange + " : "
    );
    int random_int = (int) Math.floor(
      Math.random() * (endRange - startRange + 1) + startRange
    );

    System.out.println(random_int);

    s_name.close();
  }
}

