```js
// Write a programme to find the sum of n numbers

const input = "1 2 3 4";

function sumOfNumbers(numbers) {
  let sum = 0;
  for (let i of numbers.split(" ")) {
    sum += +i;
  }
  return sum;
}

console.log("Input  :", input);
console.log("Sum    :", sumOfNumbers(input));

```

```java
// Write a programme to find sum of n numbers
import java.util.*;

class SumOfNumbers {

  public static void main(String[] args) {
    try (Scanner inp = new Scanner(System.in)) {
      System.out.print("Enter Number of Numbers to be Calculated : ");
      int n = inp.nextInt();
      int i, sum = 0, z;

      for (i = 0; i < n; i++) { //Entering N numbers
        System.out.print("Enter: ");
        z = inp.nextInt();
        sum = sum + z; //Cumulative Sum
      }

      System.out.println("\nSum of the numbers: " + sum);
    }
  }
}

```

```python
# Write a programme to find the sum of n numbers

def sum_of_number(string):
    numbers = list(string.split(" "))

    sum = 0

    for number in numbers:
        sum += int(number)

    return sum


string = "1 2 3 4"
print("Input  :", string)
print("Output :", sum_of_number(string))

```

