```cpp
// Write a programme to find volume of a cube
#include <iostream>

using namespace std;

int Vol_cube(int s)
{
    int Vol = s * s * s;
    return Vol;
}

int main()
{
    int side;
    cout << "Enter cube side : ";
    cin >> side;

    cout << "\nVolume of cube  : " << Vol_cube(side) << endl;

    return 0;
}

```

```php
<?php
// Write a program to find volume of a cube

function cube($number)
{

    if (!is_numeric($number)) {

        return 'Invalid input : Input is not a number';

    } elseif ($number < 0) {

        return "Invalid input : Input cant' be negative";

    } elseif ($number == 0) {

        return "Invalid input : Input cant' be 0";
    }

    return pow($number, 3);
}

$number = readline("Enter cube side  : ");
$cube = cube($number);

print "\nSide   of Cube : {$number}";
print "\nVolume of Cube : {$cube}\n";

```

```c
// Write a programme to find volume of a cube
#include <stdio.h>

int Vol_cube(int s)
{
  int Volume = s * s * s;
  return Volume;
}

int main()
{
  int side;
  printf("Enter cube side : ");
  scanf("%d", &side);

  printf("\nVolume of cube : %d\n", Vol_cube(side));

  return 0;
}

